import styled from "styled-components";

export const HeaderStyle = styled.header 
`background-color: #ddd;
padding: 20px 40px;
`

export const titleStyle = styled.h1`
margin-bottom: 10px;
`
export const spanStyle = styled.span
`background-color: black;
  color: white;
  padding: 3px 5px;
  border-radius: 5px;
`
